import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/authService';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token') || null);

  useEffect(() => {
    if (token) {
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        setUser(JSON.parse(storedUser));
      }
    }
  }, [token]);

  const login = async (email, password) => {
    try {
      if (email === 'admin' && password === 'admin') {
        // Для админа задаём id вручную
        const userData = { id: 1, email: 'admin', username: 'Administrator' };
        setUser(userData);
        setToken('fake-admin-token');
        localStorage.setItem('token', 'fake-admin-token');
        localStorage.setItem('user', JSON.stringify(userData));
        navigate('/profile');
      } else {
        // Предполагается, что authService.login возвращает объект, где есть поле token и данные пользователя
        const data = await authService.login(email, password);
        // Пример: data.user = { id: 42, email: 'user@example.com', username: 'User' }
        const userData = { 
          id: data.user.id, 
          email: data.user.email, 
          username: data.user.username || data.user.email 
        };
        setToken(data.token);
        localStorage.setItem('token', data.token);
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
        navigate('/profile');
      }
    } catch (error) {
      throw error;
    }
  };

  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
