  color: '#ccc',
  marginTop: '0.5rem',
  marginBottom: '1rem',
  fontSize: '1.3rem',
};

// Контейнер, где слева текст, справа квадрат
const mainContainerStyle = {
  display: 'flex',
  width: '100%',
};

// Стиль для блока с текстом и аккордами
const lyricsStyle = {
  flexGrow: 1,
  whiteSpace: 'pre-wrap',
  fontFamily: 'monospace',
  fontSize: '1.1rem',
  margin: 0,
  paddingRight: '1rem',
  overflowX: 'auto',
};

// Квадратная панель транспонирования
const transposePanelStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  width: '80px',           // фиксированная ширина
  height: '80px',          // фиксированная высота → квадрат
  backgroundColor: '#2e2f31',
  borderRadius: '6px',
  border: '1px solid #007bff',
};

// Обёртка для кнопок «−» и «+» внутри квадрата
const buttonsWrapperStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  marginBottom: '4px',
};

// Сам стиль кнопок «−»/«+» (немного меньше, чтобы вписаться в квадрат)
const transposeButtonSquareStyle = {
  width: '28px',
  height: '28px',
  margin: '1px 0',
  fontSize: '1.1rem',
  backgroundColor: '#fff',
  color: '#007bff',
  border: '2px solid #007bff',
  borderRadius: '4px',
  cursor: 'pointer',
  lineHeight: '24px',
  textAlign: 'center',
  padding: '0',
};

// Ссылка «Сбросить» под кнопками
const resetLinkStyle = {
  background: 'none',
  border: 'none',
  color: '#007bff',
  textDecoration: 'underline',
  fontSize: '0.8rem',
  cursor: 'pointer',
  padding: 0,
};

const likeButtonStyle = {
  padding: '0.5rem 1rem',
  fontSize: '1rem',
  border: 'none',
  borderRadius: '6px',
  color: '#fff',
  cursor: 'pointer',
};

export default SongDetails;
