import React, { useEffect, useState } from 'react';
import songsService from '../services/songsService';

const SongDetails = ({ songId }) => {
  const [song, setSong] = useState(null);

  useEffect(() => {
    const fetchSong = async () => {
      if (songId) {
        try {
          const data = await songsService.getSong(songId);
          setSong(data);
        } catch (error) {
          console.error("Ошибка при загрузке песни", error);
        }
      } else {
        setSong(null);
      }
    };
    fetchSong();
  }, [songId]);

  if (!song) {
    return (
      <div className="flex items-center justify-center h-full text-gray-400">
        <p>Выберите песню, чтобы увидеть текст с аккордами</p>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto bg-gray-800 p-6 rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold">{song.title}</h2>
      <h4 className="text-gray-400 text-lg">Исполнитель: {song.author}</h4>
      <pre className="bg-gray-900 text-gray-300 p-4 rounded-md mt-4 overflow-auto whitespace-pre-wrap">
        {song.lyricsWithChords}
      </pre>
    </div>
  );
};

export default SongDetails;
