import React, { useEffect, useState } from 'react';
import songsService from '../services/songsService';

const SongsList = ({ author, onSelectSong }) => {
  const [songs, setSongs] = useState([]);

  useEffect(() => {
    const fetchSongs = async () => {
      if (author) {
        try {
          const data = await songsService.getSongsByAuthor(author);
          setSongs(data);
        } catch (error) {
          console.error("Ошибка при загрузке песен", error);
        }
      }
    };
    fetchSongs();
  }, [author]);

  if (!author) {
    return (
      <div className="text-gray-400 text-center">
        <p>Выберите исполнителя, чтобы увидеть список песен</p>
      </div>
    );
  }

  return (
    <div>
      <h3 className="text-xl font-bold mb-3">Песни: {author}</h3>
      <ul className="space-y-2">
        {songs.map((song) => (
          <li
            key={song.id}
            onClick={() => onSelectSong(song)}
            className="cursor-pointer p-2 bg-gray-600 rounded-lg hover:bg-gray-500 transition"
          >
            {song.title}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SongsList;
