import React from 'react';
import Chat from '../components/Chat'; // Импортируем компонент чата

const ChatPage = () => {
  return (
    <div style={pageStyle}>
      <h1>Чат</h1>
      <Chat /> {/* Отображаем чат */}
    </div>
  );
};

const pageStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  padding: '20px',
  color: '#fff',
  backgroundColor: '#1e1e1e',
  minHeight: '100vh',
};

export default ChatPage;
