import React, { useEffect, useState } from 'react';
import songsService from '../services/songsService';

const SongsList = ({ author, onSelectSong }) => {
  const [songs, setSongs] = useState([]);

  useEffect(() => {
    const fetchSongs = async () => {
      if (author) {
        try {
          const data = await songsService.getSongsByAuthor(author);
          setSongs(data);
        } catch (error) {
          console.error("Ошибка при загрузке песен", error);
        }
      }
    };
    fetchSongs();
  }, [author]);

  if (!author) {
    return (
      <div style={placeholderStyle}>
        <p>Выберите исполнителя, чтобы увидеть список песен</p>
      </div>
    );
  }

  return (
    <div style={songsContainerStyle}>
      <h3 style={headingStyle}>Песни: {author}</h3>
      <ul style={listStyle}>
        {songs.map((song) => (
          <li 
            key={song.id} 
            onClick={() => onSelectSong(song)} 
            style={itemStyle}
          >
            {song.title}
          </li>
        ))}
      </ul>
    </div>
  );
};

const songsContainerStyle = {
  flex: 1,
  backgroundColor: '#34373b',
  padding: '1rem',
  overflowY: 'auto',
  borderRight: '1px solid #444',
};

const headingStyle = {
  marginTop: 0,
  marginBottom: '1rem',
  fontSize: '1.25rem',
};

const listStyle = {
  margin: 0,
  padding: 0,
};

const itemStyle = {
  padding: '0.5rem',
  marginBottom: '0.3rem',
  cursor: 'pointer',
  borderRadius: '4px',
  transition: 'background-color 0.2s',
  fontSize: '1rem',
};

const placeholderStyle = {
  flex: 1,
  backgroundColor: '#34373b',
  padding: '1rem',
  color: '#ccc',
  borderRight: '1px solid #444',
};

export default SongsList;
