import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import songsService from '../services/songsService';

const SongDetails = ({ songId }) => {
  const [song, setSong] = useState(null);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    const fetchSong = async () => {
      if (songId) {
        try {
          const data = await songsService.getSong(songId);
          setSong(data);
        } catch (error) {
          console.error('Ошибка при загрузке песни:', error);
        }
      } else {
        setSong(null);
      }
    };

    fetchSong();
  }, [songId]);

  const handleLike = async () => {
    if (!user) {
      return alert('Пожалуйста, авторизуйтесь');
    }

    try {
      // Используем метод по email
      await songsService.addFavoriteByEmail(user.email, song.id);
      alert('Добавлено в избранное!');
    } catch (error) {
      console.error('Ошибка:', error);
      alert('Не удалось добавить в избранное');
    }
  };

  if (!song) {
    return (
      <div style={detailsPlaceholderStyle}>
        <p>Выберите песню, чтобы увидеть текст с аккордами</p>
      </div>
    );
  }

  return (
    <div style={detailsContentWrapper}>
      <div style={detailsContentStyle}>
        <h2 style={titleStyle}>{song.title}</h2>
        <h4 style={authorStyle}>Исполнитель: {song.author}</h4>
        <pre style={lyricsStyle}>{song.lyricsWithChords}</pre>
        <button onClick={handleLike} style={likeButtonStyle}>
          Лайк
        </button>
      </div>
    </div>
  );
};

// Полный стиль компонента
const detailsContentWrapper = {
  display: 'flex',
  justifyContent: 'center',
  width: '100%',
};

const detailsContentStyle = {
  width: '100%',
  maxWidth: '800px',
  backgroundColor: '#3d4044',
  padding: '1.5rem',
  borderRadius: '8px',
  boxShadow: '0 0 10px rgba(0,0,0,0.5)',
  fontSize: '1.2rem',
  lineHeight: '1.6',
};

const detailsPlaceholderStyle = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  width: '100%',
  color: '#ccc',
};

const titleStyle = {
  marginTop: 0,
  fontSize: '2rem',
  fontWeight: 'bold',
};

const authorStyle = {
  color: '#ccc',
  marginBottom: '1rem',
  fontSize: '1.3rem',
};

const lyricsStyle = {
  whiteSpace: 'pre-wrap',
  fontFamily: 'monospace',
  fontSize: '1.2rem',
  marginTop: '1rem',
};

const likeButtonStyle = {
  marginTop: '1rem',
  padding: '10px 20px',
  backgroundColor: '#28a745',
  color: '#fff',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
};

export default SongDetails;
