import React, { useEffect, useState } from 'react';
import songsService from '../services/songsService';

const AuthorsList = ({ onSelectAuthor }) => {
  const [authors, setAuthors] = useState([]);

  useEffect(() => {
    const fetchAuthors = async () => {
      try {
        const data = await songsService.getAuthors();
        setAuthors(data);
      } catch (error) {
        console.error("Ошибка при загрузке авторов", error);
      }
    };
    fetchAuthors();
  }, []);

  return (
    <div style={sidebarStyle}>
      <h3 style={headingStyle}>Исполнители</h3>
      <ul style={listStyle}>
        {authors.map((author) => (
          <li 
            key={author} 
            onClick={() => onSelectAuthor(author)} 
            style={itemStyle}
          >
            {author}
          </li>
        ))}
      </ul>
    </div>
  );
};

const sidebarStyle = {
  width: '250px',
  backgroundColor: '#2c2f33',
  padding: '1rem',
  overflowY: 'auto',
  borderRight: '1px solid #444',
};

const headingStyle = {
  marginTop: 0,
  marginBottom: '1rem',
  fontSize: '1.25rem',
};

const listStyle = {
  margin: 0,
  padding: 0
};

const itemStyle = {
  padding: '0.5rem',
  marginBottom: '0.3rem',
  cursor: 'pointer',
  borderRadius: '4px',
  transition: 'background-color 0.2s',
  fontSize: '1rem',
};

/* 
   Можно добавить hover-эффект через CSS-класс, 
   но для простоты используем inline-стили с onMouseEnter/onMouseLeave
   или в отдельном .css файле.

   Если хотите inline:
   
   <li
     onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#444'}
     onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
     ...
   >
*/

export default AuthorsList;
