import React, { useEffect, useState } from 'react';
import songsService from '../services/songsService';

const AuthorsList = ({ onSelectAuthor }) => {
  const [authors, setAuthors] = useState([]);

  useEffect(() => {
    const fetchAuthors = async () => {
      try {
        const data = await songsService.getAuthors();
        setAuthors(data);
      } catch (error) {
        console.error("Ошибка при загрузке авторов", error);
      }
    };
    fetchAuthors();
  }, []);

  return (
    <div style={sidebarStyle}>
      <h3>Авторы</h3>
      <ul style={listStyle}>
        {authors.map((author) => (
          <li key={author.id} onClick={() => onSelectAuthor(author)} style={itemStyle}>
            {author.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

const sidebarStyle = {
  width: '250px',
  backgroundColor: '#222',
  color: '#fff',
  padding: '10px',
  height: '100vh',
  overflowY: 'auto'
};

const listStyle = {
  listStyleType: 'none',
  padding: 0
};

const itemStyle = {
  padding: '5px',
  cursor: 'pointer'
};

export default AuthorsList;
