import React, { useContext, useEffect, useState } from 'react';
import songsService from '../services/songsService';
import { AuthContext } from '../context/AuthContext';

const SongDetails = ({ songId }) => {
  const { user } = useContext(AuthContext);
  const [song, setSong] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    console.log('SongDetails: user =', user); // Добавьте для отладки
    const fetchSong = async () => {
      if (songId) {
        try {
          const data = await songsService.getSong(songId);
          setSong(data);
          if (user && user.id) {
            const favorites = await songsService.getFavorites(user.id);
            setIsFavorite(favorites.some(fav => fav.id === songId));
          } else {
            console.error('Пользователь не найден или user.id отсутствует');
          }
        } catch (error) {
          console.error("Ошибка при загрузке песни", error);
        }
      } else {
        setSong(null);
      }
    };
    fetchSong();
  }, [songId, user]);

  const toggleFavorite = async () => {
    if (!user || !user.id) {
      console.error("Пользователь не авторизован или отсутствует user.id");
      return;
    }
    try {
      if (isFavorite) {
        await songsService.removeFavorite(user.id, songId);
      } else {
        await songsService.addFavorite(user.id, songId);
      }
      setIsFavorite(!isFavorite);
    } catch (error) {
      console.error("Ошибка при обновлении избранного", error);
    }
  };

  if (!song) {
    return (
      <div style={detailsPlaceholderStyle}>
        <p>Выберите песню, чтобы увидеть текст с аккордами</p>
      </div>
    );
  }

  return (
    <div style={detailsContentWrapper}>
      <div style={detailsContentStyle}>
        <div style={headerStyle}>
          <h2 style={titleStyle}>{song.title}</h2>
          <button onClick={toggleFavorite} style={getFavoriteButtonStyle(isFavorite)}>
            {isFavorite ? 'Удалить из избранного' : 'Добавить в избранное'}
          </button>
        </div>
        <h4 style={authorStyle}>Исполнитель: {song.author}</h4>
        <pre style={lyricsStyle}>{song.lyricsWithChords}</pre>
      </div>
    </div>
  );
};

/* Стили, как и ранее */
const detailsContentWrapper = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  width: '100%',
  height: '100vh',
  padding: '20px',
};

const detailsContentStyle = {
  width: '100%',
  maxWidth: '800px',
  backgroundColor: '#3d4044',
  padding: '1.5rem',
  borderRadius: '8px',
  boxShadow: '0 0 10px rgba(0,0,0,0.5)',
  fontSize: '1.2rem',
  lineHeight: '1.6',
};

const detailsPlaceholderStyle = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  width: '100%',
  height: '100vh',
  color: '#ccc',
};

const headerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: '1rem',
};

const titleStyle = {
  marginTop: 0,
  fontSize: '2rem',
  fontWeight: 'bold',
};

const authorStyle = {
  color: '#ccc',
  marginBottom: '1rem',
  fontSize: '1.3rem',
};

const lyricsStyle = {
  whiteSpace: 'pre-wrap',
  fontFamily: 'monospace',
  fontSize: '1.2rem',
  marginTop: '1rem',
};

const getFavoriteButtonStyle = (isFavorite) => ({
  backgroundColor: isFavorite ? '#e74c3c' : '#3498db',
  color: '#fff',
  border: 'none',
  padding: '10px 15px',
  borderRadius: '5px',
  cursor: 'pointer',
});

export default SongDetails;
