import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/authService';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token') || null);

  useEffect(() => {
    if (token) {
      try {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
          setUser(JSON.parse(storedUser));
        }
      } catch (error) {
        console.error("Ошибка при загрузке пользователя из localStorage:", error);
        setUser(null);
        localStorage.removeItem('user');
      }
    }
  }, [token]);

  const login = async (email, password) => {
    try {
      if (email === 'admin' && password === 'admin') {
        const userData = { id: 1, email: 'admin', username: 'Administrator', role: 'Admin' }; // ✅ роль
        setUser(userData);
        setToken('fake-admin-token');
        localStorage.setItem('token', 'fake-admin-token');
        localStorage.setItem('user', JSON.stringify(userData));
        navigate('/profile');
        return;
      }

      const data = await authService.login(email, password);
      const returnedToken = data.Token || data.token;
      if (!returnedToken) {
        throw new Error("Некорректный ответ сервера");
      }

      const userData = {
        id: 0,
        email: email,
        username: email,
        role: 'User' // ✅ роль обычного юзера
      };

      setToken(returnedToken);
      localStorage.setItem('token', returnedToken);
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      navigate('/profile');
    } catch (error) {
      console.error("Ошибка при входе:", error);
      throw error;
    }
  };

  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
