import React, { useState } from 'react';
import AuthorsList from '../components/AuthorsList';
import SongsList from '../components/SongsList';
import SongDetails from '../components/SongDetails';

const HomePage = () => {
  const [selectedAuthor, setSelectedAuthor] = useState(null);
  const [selectedSong, setSelectedSong] = useState(null);

  const handleSelectAuthor = (author) => {
    setSelectedAuthor(author);
    setSelectedSong(null);
  };

  const handleSelectSong = (song) => {
    setSelectedSong(song);
  };

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white font-sans">
      {/* Левая колонка: исполнители */}
      <div className="w-1/4 bg-gray-800 border-r border-gray-700 p-4">
        <AuthorsList onSelectAuthor={handleSelectAuthor} />
      </div>

      {/* Средняя колонка: песни */}
      <div className="w-1/4 bg-gray-700 border-r border-gray-600 p-4">
        <SongsList author={selectedAuthor} onSelectSong={handleSelectSong} />
      </div>

      {/* Правая колонка: детали песни */}
      <div className="flex-1 p-6">
        <SongDetails songId={selectedSong ? selectedSong.id : null} />
      </div>
    </div>
  );
};

export default HomePage;
